import styled from 'styled-components'

export const LandingStyle = styled.div`

    background: url('https://www.indochinatour.com/assets/images/Vietnam--Cambodia--Laos/backpacking-banner-vcl.jpg');
    background-repeat: round;
    display: flex;
    height: 100vh;
    justify-content:center;
    margin:auto;

    .general {
        margin-top:15%;
    };

    p {
        color:#011936;
        width: 600px;
        margin-left:15%;
        font-size: 40px;
        font-weight: 600;
    };

    button {
        background-color: #04724d;
        border: none;
        color: white;
        padding: 10px 12px;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        font-size: 10px;
        font-weight: 600;
        margin-left:220px;
        border-radius: 10px;
    };
`;
